con:config.script:
import groovy.json.JsonSlurper

def response =testRunner.testCase.testSteps["POSTAddproductcategory"].testRequest.response.contentAsString

def json = new JsonSlurper().parseText(response)

def categoryid=json.data.id.toString()

testRunner.testCase.getTestStepByName("Properties").setPropertyValue("categoryid",categoryid)

